package com.example.util;public class HTMLUtil {    public static String getRegistrationButton(String url) {        return "<!DOCTYPE html> " +                "<html> " +                "<head> " +                "  <title>Beautiful Button</title> " +                "  <style> " +                "    body { " +                "      display: flex; " +                "      justify-content: center; " +                "      align-items: center; " +                "      height: 100vh; " +                "      margin: 0; " +                "      text-align: center;" +                "    } " +                "    .button { " +                "      display: inline-block; " +                "      padding: 15px 30px; " +                "      font-size: 18px; " +                "      font-weight: bold; " +                "      text-align: center; " +                "      text-decoration: none; " +                "      background-image: linear-gradient(to right, #4CAF50, #45a049); " +                "      color: #ffffff; " +                "      border-radius: 30px; " +                "      box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1); " +                "      transition: transform 0.3s, box-shadow 0.3s, background-image 0.3s; " +                "    } " +                "    .button:hover { " +                "      transform: translateY(-2px); " +                "      box-shadow: 0px 6px 12px rgba(0, 0, 0, 0.2); " +                "      background-image: linear-gradient(to right, #45a049, #4CAF50); " +                "    } " +                "    .button:active { " +                "      transform: translateY(0); " +                "      box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1); " +                "    } " +                "  </style> " +                "</head> " +                "<body> " +                String.format("<a href=\"%s\" class=\"button\">Click link to complete registration </a> ", url) +                "</body> " +                "</html> ";    }    public static String getSuccessUpdatedEmail(String url) {        return "<!DOCTYPE html> " +                "<html> " +                "<head> " +                "  <title>Beautiful Button</title> " +                "  <style> " +                "    body { " +                "      display: flex; " +                "      justify-content: center; " +                "      align-items: center; " +                "      height: 100vh; " +                "      margin: 0; " +                "      text-align: center;" +                "    } " +                "    .button { " +                "      display: inline-block; " +                "      padding: 15px 30px; " +                "      font-size: 18px; " +                "      font-weight: bold; " +                "      text-align: center; " +                "      text-decoration: none; " +                "      background-image: linear-gradient(to right, #4CAF50, #45a049); " +                "      color: #ffffff; " +                "      border-radius: 30px; " +                "      box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1); " +                "      transition: transform 0.3s, box-shadow 0.3s, background-image 0.3s; " +                "    } " +                "    .button:hover { " +                "      transform: translateY(-2px); " +                "      box-shadow: 0px 6px 12px rgba(0, 0, 0, 0.2); " +                "      background-image: linear-gradient(to right, #45a049, #4CAF50); " +                "    } " +                "    .button:active { " +                "      transform: translateY(0); " +                "      box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1); " +                "    } " +                "  </style> " +                "</head> " +                "<body> " +                String.format("<a href=\"%s\" class=\"button\">CLICK LINK FOR UPDATE EMAIL </a> ", url) +                "</body> " +                "</html> ";    }    public static String getResponse() {        return "<!DOCTYPE html> " +                "<html> " +                "<head> " +                "  <title>Registration Completed</title> " +                "  <style> " +                "    body { " +                "      display: flex; " +                "      justify-content: center; " +                "      align-items: center; " +                "      height: 100vh; " +                "      margin: 0; " +                "      background-color: #f2f2f2; " +                "    } " +                " " +                "    .message { " +                "      font-size: 36px; " +                "      font-weight: bold; " +                "      color: #007BFF; " +                "      animation: fadeIn 2s ease-in-out; " +                "      transition: background-color 0.3s; " +                "    } " +                "    .message:hover { " +                "      background-color: #FFC107; " +                "    } " +                " " +                "    @keyframes fadeIn { " +                "      from { " +                "        opacity: 0; " +                "      } " +                "      to { " +                "        opacity: 1; " +                "      } " +                "    } " +                "    </style> " +                "    </head> " +                "    <body> " +                "    <p class=\"message\">Congratulations! Update completed successfully!</p> " +                "    </body> " +                "    </html>";    }}