select v.*from video v         inner join playlist_video pv                    on pv.video_id = v.idwhere pv.playlist_id = 1;select *from video as v         inner join playlist_video as pv on pv.video_id = v.id;select pv.video_idfrom playlist_video pv         inner join video v on pv.video_id = v.idwhere pv.playlist_id = :playlistId  and v.video_status = 'PUBLISHED';select pv.playlist_id,       v.id,       pa.id,       pa.url,       v.title,       a.duration,       ch.id,       ch.name,       pv.created_date,       pv.order_numfrom playlist_video pv         inner join video v on pv.video_id = v.id         inner join attach a on a.id = v.id         inner join preview_attach pa on v.preview_attach_id = pa.id         inner join channel ch on v.channel_id = ch.idwhere v.video_kind like 'PUBLISHED';